import React, { useEffect, useState } from 'react'
import { useNavigate } from "react-router-dom";
import { PokemonContainer } from '../../components/PokemonContainer/PokemonContainer'
import { Pagination } from '../../components/Pagination/Pagination';

export const HomePage = () => {
	const navigate = useNavigate();
	const [listPokemons, setListPokemons] = useState([]);
	const [limit, setLimit] = useState(20);

	useEffect(() =>{
		(
			async function(){
					try{
						const response = await fetch(`https://pokeapi.co/api/v2/pokemon/?offset=${limit}&limit=20`);
						const data = await response.json();
						setListPokemons(data.results)
						console.log(data.results)
					}catch(err){
						console.log("err")
					}
			}
		)()
	},[limit])


  return (
    <div>
			{
				listPokemons.map((pok)=>
					<PokemonContainer onClick={()=> navigate("/detail")} name={pok.name}/>
				)
			}
			<Pagination limit={limit} onClick={()=>setLimit(limit+20)}/>
    </div>
  )
}
