import { createStoreHook} from 'react-redux'

const initialState = {
    limit: 20,
    offset:20
}

const listReducer =(state = initialState, action) =>{
    switch(action.type){
        case '@list/limit/incremented':
            return state.limit + 20
        case '@list/limit/decremented':
            return state.limit + 20
        default:
            return state
    }
}

export const actionIncremented= {
    type: '@list/limit/incremented'
}

export const actionDecremented= {
    type: '@list/limit/decremented'
}

export const store = createStoreHook(listReducer);
